import React,{Component}from 'react';
import axios from 'axios';


export default class Download extends Component{
  constructor(props){
    super(props);
    this.state={
      File:null
    }
    this.Onsubmit=this.Onsubmit.bind(this);
    this.onChange=this.onChange(this);
  }
  
  onChange(e) {
    this.setState({File:e.target.file});
  }
  Onsubmit(){
      var formData = new FormData();
      var imagefile = document.querySelector('#file');
      formData.append("image", imagefile.File[0]);
      axios.post('http://localhost:3000/upload', formData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
      })
  }
  render(){
      return (
                <div className="container">
                  <div className="row">
                    <div className="col-md-6 m-auto">
                    <h1 className="text-center display-4 my-4"> Upload File</h1>
                    {/* <form action="http://localhost:3000/upload" method="POST" encType="multipart/form-data"> */}
                    <form>
                      <div className="custom-file mb-3">
                        <input type="file" name="file" id="file" onChange={onChange} className="custom-file-label"/>
                          <label htmlFor="file" className="custom-file-label">Chose</label>
                      </div>
                      <input onClick={this.Onsubmit} value="Submit" className=" btn btn-primary btn-block"/>
                    </form>
                    </div>
                  </div>
                </div>
              )
           }
  }



