import React from 'react';
import Login from '../components/UserComponent/Login.component';
import Signup from '../components/UserComponent/createUser.component';

class Login_Signup extends React.Component{
    constructor(props){
        super(props);
        this.state={flag:false};
        this.ToLogin=this.ToLogin.bind(this);
    }

    componentDidMount(){
            this.setState({flag:!this.state.flag})
    }
    ToLogin(flag)
    {
        this.setState({flag:flag})
    }
    render()
    {
        let Button='false';
        return(
            <div className="text-center container" >
                <div>
                    {this.state.flag?
                    <div>
                        <Login className="container" Auth={this.props.setUser}/>
                        {Button="To Sign Up"}
                    </div>
                    :<div>
                        <Signup className="container" Auth={this.props.setUser}/>
                        {Button="To Login"}
                    </div>}
                    <br/>
                    <button  onClick={()=>{this.ToLogin(!this.state.flag)}} className="btn btn-outline-info btn-block"></button>
               </div>
            </div>
            )
    }
}
export default Login_Signup;
