import React,{Component, Fragment}from 'react';
 import axios from 'axios';
import Chart from './chart.component';

export default class AdminSpecs extends Component{
   constructor(props){
       super(props);
       this.state={
        chartData:{},
        Requests:[],
        SvmData:[],
       }
       this.SetData=this.SetData.bind(this)
}
ReqForSvm(Svm){
    axios.get(`http://localhost:3000/Request/Svm/${Svm}`)
        .then((res)=>{this.SetData(res.data)
        })
}
SetChartData(SvmData){
    console.log(SvmData.Req)
    let Responses=[0,0,0]
    SvmData.forEach(Req => {
        if(Req.status==="pending")
            Responses[0]++;
        if(Req.status==='Accepted')
            Responses[1]++;
        if(Req.status==="Decline")
            Responses[2]++;
    });
    return Responses
}
 SetData(Svm){ 
    let SvmData=this.SetChartData(Svm)
    return({
        chartData:{
            labels:['Freespace','UsedSpace'],
            datasets:[{
             label:"Requests",
             backgroundColor:["rgba(200, 0, 120, 0.65)",
                              "rgba(60, 0, 200, 0.65)",
                              "rgba(255, 12, 60, 0.75)",
                              ],
             data:SvmData
            },
            ]
        }
    })
 }
 Charts(){
    return(
        this.props.SVMS.map((currntSvm,index)=>{
            this.ReqForSvm(currntSvm)
            return(
                    <div className="col-md-6" key={index}>
                        <Chart type='Pie' title={currntSvm}chartData={this.SetData()}/>
                    </div>
                )
        })
    )

 }

    render(){
        console.log(this.props)
        return(
            <Fragment>
                {this.Charts()}
            </Fragment>
        )
    }
}
