import React,{Component}from 'react';
import Login from '../components/UserComponent/Login.component';
import Signup from '../components/UserComponent/createUser.component';

export default class Login_Signup extends Component{
    constructor(props){
        super(props);
        this.state={
            status:false,
            mod:'Login',
            err:''
        }
        
        this.Auth=this.Auth.bind(this);
        this.handelmod=this.handelmod.bind(this);
    }
    Auth(data){
        console.log(data)
        this.props.setUser(data);
       // window.location='/MyTodos';
    }

    handelmod(e){
        this.setState({status:e})
    }  
    render(){
        const flag=this.state.status;
        return(
            <div className="text-center container" >
                    {flag?
                    <div>
                        <Login err={this.state.err} className="container" Auth={this.Auth}/>
                        <button className="btn btn-outline-info btn-block" onClick={this.handelmod(true)}>To SignUp</button>
                    </div>
                    :<div>
                        <Signup className="container" Auth={this.Auth}/>
                        <button className="btn btn-outline-info btn-block" onClick={this.handelmod(false)}>To Login</button>
                    </div>}
            </div>
        )
    }
}
