import React,{Component}from 'react';
import axios from 'axios';

class Admin extends Component{
    constructor(props){
        super(props);
        this.state={
            Users:[],
            Requsts:[],
            Bugs:[]
            
        }
    }
    async dataAsamble(){
        let Users;
        let Requsts;
        let Bugs;
        await axios.get("http://localhost:3000/users/")
            .then((res)=>Users=res.data);
        await axios.get(`http://localhost:3000/Request/`)
            .then((res)=>Requsts=res.data)
        await axios.get(`http://localhost:3000/Bug/`)
            .then((res)=>Bugs=res.data)
        this.setState({
            Users:Users,
            Requsts:Requsts,
            Bugs:Bugs
        })
    }
    componentDidMount(){
        this.dataAsamble()
    }
