import React,{Component}from 'react';
import axios from 'axios';
import SystemGrath from '../components/SystemGrafth.component'
import { withRouter } from "react-router-dom";

class ArcitectPage extends Component{
    constructor(props){
        super(props);
        this.state={
            username:'',
            email:'',
            Svm:[],
            File:''
        }
        this.onClickHendler=this.onClickHendler.bind(this);
        this.UptateStatus=this.UptateStatus.bind(this);
    }
    onClickHendler(){
        console.log(this.state.File);
        axios.get(`http://localhost:3000/File/${this.state.File}`)
        .then(res=>{console.log(res)})
    }
    UptateStatus(status){
        const Status={status:status};
        axios.post(`http://localhost:3000/Request/status/${this.props.match.params.id}`,Status)
        .then(res=>{this.props.history.push("/Architect");})

    }
    componentDidMount(){
        //getRequestByID
        console.log(this.props.match.params.id)
        axios.get(`http://localhost:3000/Request/${this.props.match.params.id}`)
        .then(res=>{
            this.setState({
                username:res.data.Req.username,
                email:res.data.Req.email,
                Svm:res.data.Req.SVM,
                File:res.data.Req.File,
                key:res.data.Req._id
            });
        })
    }
    
    render(){
        let SVMS=[];
        SVMS[0]=this.state.Svm
        let downlodURI=`http://localhost:3000/File/${this.state.File}`;
        return(
             <div class="card text-center">
                <div class="card-header">
                Storage Request
                </div>
                <div className='row'>
                    <div className="col-md-6">
                            <div class="card-body">
                            <h5 class="card-title">{this.state.username}</h5>
                            <p class="card-text">{this.state.email}<br/>{this.state.Svm} </p>
                            <a href={downlodURI} className='btn btn-primary' download>Download File</a>
                        </div>
                        <div class="card-footer text-muted">
                            <div className="d-flex justify-content-center">
                                <button onClick={()=>this.UptateStatus('Accepted')} className="btn btn-success pr-2">Accept</button>
                                <button onClick={()=>this.UptateStatus('Decline')}className="btn btn-danger">Decline</button>
                            </div>
                        </div>
                    </div>
                    <SystemGrath SVMS={SVMS}/>
                </div>
            </div>
        )
    }
}

export default withRouter(ArcitectPage)
