 import axios from 'axios';
import Chart from './chart.component';

export default class SystemGrafth extends Component{
   constructor(props){
       super(props);
       this.state={
        chartData:{n:0},
        Requests:[],
        SVM:'',
       }
    //    this.setdata=this.setdata.bind(this);
}

async SetData(){ 
    let value;
    let array=[0,1000];
    console.log(value)
    this.setState({
        chartData:{
            labels:['Free','Used'],
            datasets:[{
             label:"Requests",
             backgroundColor:["rgba(175, 146, 234, 0.65)",
                              "rgba(245, 73, 120, 0.65)",
                              ],
             data:array
            },
            ]
        }
    })
 }
componentDidMount(){
    // if(this.props!==nextProps){
    let SVMS=[];
    console.log(this.state.SVM)
