    //         .then((res)=>{ 
    //             let Amount=0;
    //             res.data.Req.forEach(Req => {
    //                 Amount+=Req.Amount;
    //             });
    //             SVMS.push(Amount);
    //         }) 
    //         console.log(SVMS)
    //     this.setState({SvmData:SVMS})
    // });
    console.log(this.props.SVMS)
    //}   
}
 Charts(){
    return(
        this.props.SVMS.map((currntSvm,index)=>{
            if(this.state.chartData.n===0)
                {
                    this.SetData();
                    this.setState({SVM:currntSvm})
                }
                
            return(
                    <div className="col-md-6" key={index}>
                        {this.state.chartData.datasets?
                             <Chart type='Pie' title={currntSvm} chartData={this.state}/>
                        :null}
                    </div>
                )
        })
    )

 }

    render(){
        return(
            <Fragment>
                {this.Charts()}
            </Fragment>
        )
    }
}
