import React,{Component, Fragment}from 'react';
 import axios from 'axios';
import Chart from './chart.component';

export default class SystemGrafth extends Component{
   constructor(props){
       super(props);
       this.state={
        chartData:{},
        Requests:[],
        SvmData:[],
       }
}
async SetData(Svm){ 
    let value=this.props.setdata();
    console.log(value)
    return({
        chartData:{
            labels:['Free','Used'],
            datasets:[{
             label:"Requests",
             backgroundColor:["rgba(175, 146, 234, 0.65)",
                              "rgba(245, 73, 120, 0.65)",
                              ],
             data:[3,2]
            },
            ]
        }
    })
 }

 Charts(){
    return(
        this.props.SVMS.map((currntSvm,index)=>{
            return(
                    <div className="col-md-6" key={index}>
                        <Chart type='Pie' title={currntSvm} chartData={this.SetData({currntSvm})}/>
                    </div>
                )
        })
    )

 }

    render(){
        return(
            <Fragment>
                {this.Charts()}
            </Fragment>
        )
    }
}
