import React,{Component}from 'react';
import Login from '../components/UserComponent/Login.component';
import Signup from '../components/UserComponent/createUser.component';

export default class Login_Signup extends Component{
    constructor(props){
        super(props);
        this.state={
            flag:false
        }
    }

    render(){
        return(
            <div className="text-center container" >
                    {this.state.flag?
                    <div>
                        <Login err={this.state.err} className="container" Auth={this.props.setUser}/>
                        <button className="btn btn-outline-info btn-block" onClick={this.setState({flag:false})}>To SignUp</button>
                    </div>
                    :<div>
                        <Signup className="container" Auth={this.props.setUser}/>
                        <button className="btn btn-outline-info btn-block" onClick={this.setState({flag:true})}>To Login</button>
                    </div>}
            </div>
        )
    }
}
