import React,{Component}from 'react';
import axios from 'axios';
import Chart from '../components/chart.component';
// import SystemGrath from'../components/SystemGrafth.component'
import '../components/Admin.css'
class Admin extends Component{
    constructor(props){
        super(props);
        this.state={
            Users:[],
            Requsts:[],
            Bugs:[],
            SVMS:["coconut","lime","grapefruit","mango"],
            flag:0
        }
        this.setflag=this.setflag.bind(this);
    }
    setflag(e){
        this.setflag({
            setState:e.target.value
        })
    }
    SetData(){ 
        let Responses=[0,3,1,5]
        return({
            chartData:{
                labels:['pending for response','accepted','declined'],
                datasets:[{
                 label:"Requests",
                 backgroundColor:["rgba(200, 200, 15, 0.65)",
                                  "rgba(100, 200, 10, 0.65)",
                                  "rgba(255, 12, 60, 0.75)",
                                  ],
                 data:Responses
                },
                ]
            }
        })
     }
    async dataAsamble(){
        let Users;
        let Requsts;
        let Bugs;
        await axios.get("http://localhost:3000/users/")
            .then((res)=>Users=res.data);
        await axios.get(`http://localhost:3000/Request/`)
            .then((res)=>Requsts=res.data)
        await axios.get(`http://localhost:3000/Bug/`)
            .then((res)=>Bugs=res.data)
        this.setState({
            Users:Users,
            Requsts:Requsts,
            Bugs:Bugs,
        })
    }
    componentDidMount(){
        this.dataAsamble()
    }
    BugTable(){
        return this.state.Bugs.map(CurrentBug=>{
            const status=CurrentBug.status;
            const downloadURI=`http://localhost:3000/File/${CurrentBug.File}`;
            return((status==="pending")?
                        <tr key={CurrentBug._id}>
                            <td>{CurrentBug._id}</td>
                            <td>{CurrentBug.username}</td>
                            <td>{CurrentBug.email}</td>
                            <td>{CurrentBug.Description}</td>
                            <td><a href={downloadURI} className="btn btn-success" download>download</a></td>
                        </tr>:null)
                    })
                }
    UserTable(){
        return this.state.Users.map(currentUser=>{
            return(
                <tr key={currentUser._id}>
                    <td>{currentUser._id}</td>
                    <td>{currentUser.username}</td>
                    <td>{currentUser.email}</td>
                    <td>{currentUser.Nickname}</td>
                    <td>{currentUser.Team}</td>
                </tr>
                    )
                })
            }
render(){
    console.log(this.state)
    return(
    <>
    <div className="cuntiner-fluid">
        <div className="row">
            <nav id="sidebarMenu" className="col-md-3 col-lg-2 d-md-block bg-light sidebar collapse">
                <div className="sidebar-sticky pt-3">
                    <ul className="nav flex-column ">
                        <li className="navbar-item">
                            <a href="/Admin" className="nav-link active">
                                <svg width="1em" height="1em" viewBox="0 0 16 16" className="bi bi-house" fill="currentColor" xmlns="http://www.w3.org/2000/svg">
                                    <path fillRule="evenodd" d="M2 13.5V7h1v6.5a.5.5 0 0 0 .5.5h9a.5.5 0 0 0 .5-.5V7h1v6.5a1.5 1.5 0 0 1-1.5 1.5h-9A1.5 1.5 0 0 1 2 13.5zm11-11V6l-2-2V2.5a.5.5 0 0 1 .5-.5h1a.5.5 0 0 1 .5.5z"/>
                                    <path fillRule="evenodd" d="M7.293 1.5a1 1 0 0 1 1.414 0l6.647 6.646a.5.5 0 0 1-.708.708L8 2.207 1.354 8.854a.5.5 0 1 1-.708-.708L7.293 1.5z"/>
                                </svg>
                                {" home"}
                            </a>
                        </li>
                        <li className="navbar-item">
                            <a href="#" className="nav-link active">
