import React,{Component}from 'react';
import axios from 'axios';
import { withRouter } from "react-router-dom";

class createUser extends Component{
    
    constructor(props){
        super(props);
        this.onChangeUsername=this.onChangeUsername.bind(this);
        this.onChangeemail=this.onChangeemail.bind(this);
        this.onChangePassword=this.onChangePassword.bind(this);
        this.onChangeTeam=this.onChangeTeam.bind(this);
        this.onChangNickName=this.onChangNickName.bind(this);
        this.onsubmit=this.onsubmit.bind(this);
        
        this.state={
            username:'',
            NickName:'',
            email:'',
            password:'',
            Team:'',
        }
    }
    // handeling form fields
    onChangeUsername(e){
        this.setState({
            username:e.target.value
        });
    }
    onChangNickName(e){
        this.setState({
            NickName:e.target.value
        })
    }
    onChangeTeam(e){
        this.setState({
            Team:e.target.value
        });
    }
    onChangeemail(e){
        this.setState({
            email:e.target.value
        });
    }
    onChangePassword(e){
        this.setState({
            password:e.target.value
        });
    }
    // submiting the form and creating new user in the server
    onsubmit(e){
        e.preventDefault();
        const user={
            username:this.state.username,
            NickName:this.state.NickName,
            email:this.state.email,
            password:this.state.password,
            Team:this.state.Team,
        }

        axios.post('users/add',user)
            .then(async res=>{
                if(res.data.err===null)
                {
                    let ts=Date.now()
                    let date_ob = new Date(ts);
                    let day= date_ob.getDate();
                    let month = date_ob.getMonth() + 1;
                    let year = date_ob.getFullYear();
                    await axios.post(`MonthDateRoute/users/${month}-${day}-${year}`)
                        // .then((res)=>{console.log(res)})
                        
                        if(res.data.res)
                        {
                        localStorage.setItem('user', JSON.stringify(res.data.res));
                        console.log(res.data)
                    // if(res.data.Admin){
                    //    window.location.href="/admin";
                    // }else if(res.data.Architect){
                    //     window.location.href="/Architect";
                    // }else{
                    //     window.location.href="/Home";
                    // }
                    }
                }else{
                    this.props.msg('please choose other urename/email already exists');
                }
            })
            .catch(err=>{this.props.msg('UserName or password is incurrect')});
    }
    // rendering the formn
    render(){
        return(
            <div>
            <h3>SignUp</h3>
            <form onSubmit={this.onsubmit}>
                <div className="form-group">
                    <label>Username:</label>
                    <input type='text' required
                    className="form-control" value={this.state.username}
                    onChange={this.onChangeUsername}/>
                </div>
                <div className="form-group">
                    <label>NickName:</label>
                    <input type='text' required
                    className="form-control" value={this.state.NickName}
                    onChange={this.onChangNickName}/>
                </div>
                <div className="form-group">
                    <label>Hail:</label>
                    <select className="custom-select" required value={this.state.Team} onChange={this.onChangeTeam}>
                    <option value="" defaultValue disabled>--chose Hail--</option> 
                    <option value="1">חיל האוויר הישראלי</option>
                    <option value="2">חיל ההנדסה הישראלי</option>
                    <option value="3">חיל החינוך והנוער‏</option>
                    <option value="4">חיל השלישות‏</option>
                    <option value="5">חיל החימוש</option>
                    <option value="6">חיל הים הישראלי</option>
                    <option value="7">חיל  הלוגיסטיקה‏</option>
                    <option value="8">חיל המשטרה הצבאית</option>
                    <option value="9">חיל נשים</option>
                    <option value="10">קציני חילות ראשיים בצה"ל</option>
                    <option value="11">חיל הקשר והתקשוב</option>
                    <option value="12">חיל הרגלים הישראלי</option>
                    <option value="13">חיל הרפואה</option>
                    <option value="14">חיל השריון הישראלי</option>
                    <option value="15">חיל התותחנים הישראלי‏</option>

                    </select>
                </div>
                <div className="form-group">
                    <label>email:</label>
                    <input type='email' required
                    className="form-control" value={this.state.email}
                    onChange={this.onChangeemail}/>
                </div>
                <div className="form-group">
                    <label>password:</label>
                    <input type='password' required
                    className="form-control" value={this.state.password}
                    onChange={this.onChangePassword}/>
                </div>
                <div>
                    <button type="submit" className="btn btn-outline-primary btn-block">createUser</button>
                </div>
            </form>
        </div>
        )
    }
}
export default withRouter(createUser)
