import React, { Component} from'react'
import axios from 'axios'

class StorageGraft extends Component{
    constructor(props){
        super(props);
        this.state={
            Aggre:'',
            SVMSArray:[],
        }
    }
    componentDidMount(){
        let Aggre;
        let SVMSArray;
        console.log(this.props)
        axios.get(`http://localhost:3000/Aggregate/Aggre/${this.props.Aggre}`)
        .then(async(res)=>{
            Aggre=res.data[0];
            await axios.get(`http://localhost:3000/SvmRoute/SvmByAggreName/${this.props.Aggre}`)
            .then((res)=>{
            console.log(res.data)
            SVMSArray=res.data

            });
            this.setState({Aggre:Aggre,SVMSArray:SVMSArray})
         });
    
    }
    getBar(){
        return(
            <div className="progress-bar progress-bar-striped bg-success"
            role="progressbar"
            style={{width:100}}>
                {100}%
        </div>
        )
    }
    render(){
        console.log(this.state.Aggre)
        return(
            <>
            {this.state.Aggre?
                <div className="progress">
                    {this.state.Aggre.Amount!==0?
                    <>
                        {this.state.Aggre.TotalAmount!==0?
                            <div className="progress-bar progress-bar-striped bg-success"
                                role="progressbar"
                                style={{width:`${((this.state.Aggre.Amount/this.state.Aggre.TotalAmount)*-100)+100}`}}>
                                    {((this.state.Aggre.Amount/this.state.Aggre.TotalAmount)*-100)+100}%
                            </div>
                        :       <div className="progress-bar progress-bar-striped bg-danger"
                                role="progressbar"
                                style={{width:`${1000}`}}>
                                {Error}%
                                </div>
                            }
                    </>
                    :<div className="progress-bar progress-bar-striped bg-success"
                             role="progressbar"
                             style={{width:`${100}`}}>
                                {100}%
                        </div>
                    }
                    {this.getBar()}
                </div>
                :
                <div className="spinner-grow spinner-grow-sm" role="status">
                    <span className="sr-only">Loading...</span>
                </div>
                }
            </>
            );
    }

}
export default StorageGraft
