import React,{Component}from 'react';
import axios from 'axios';
import BugRow from '../components/BugRow.component'
import SystemGrath from'../components/SystemGrafth.component'
class Admin extends Component{
    constructor(props){
        super(props);
        this.state={
            Users:[],
            Requsts:[],
            Bugs:[]
            
        }
    }
    async dataAsamble(){
        let Users;
        let Requsts;
        let Bugs;
        await axios.get("http://localhost:3000/users/")
            .then((res)=>Users=res.data);
        await axios.get(`http://localhost:3000/Request/`)
            .then((res)=>Requsts=res.data)
        await axios.get(`http://localhost:3000/Bug/`)
            .then((res)=>Bugs=res.data)
        this.setState({
            Users:Users,
            Requsts:Requsts,
            Bugs:Bugs
        })
    }
    componentDidMount(){
        this.dataAsamble()
    }
    BugTable(){
        return this.state.Bugs.map(CurrentBug=>{
            const status=CurrentBug.status;
            return((status==="pending")?
                        <tbody className="table-striped col-md-3 mt-3"  key={CurrentBug._id}>
                            <BugRow Username={CurrentBug.username}  email={CurrentBug.email}
                            Description={CurrentBug.Description} File={CurrentBug.File}
                            id={CurrentBug._id}/>
                        </tbody>:null)
                    })
                }
render(){
    console.log(this.state)
    return(
    <div className="container-fluid d-flex justify-content-center">
            <div >
            <div className="jumbotron jumbotron-fluid">
                <div className="container">
                    <h1 className="display-4">Hello {this.props.User.username}</h1>
                        <p className="lead">System status</p>
                    </div>
                </div>
                <div>
                    <table className="table table-hover ">
                        <thead>
                            <tr className="table-primary">
                                <th scope="col"> Bug NUMBER</th>
                                <th scope="col">Username</th>
                                <th scope="col">email</th>
                                <th scope="col">Description</th>
                                <th scope="col">Download</th>
                            </tr>
                        </thead>
                            {this.BugTable()}
                    </table>
                </div>
                <div>
                    <SystemGrath/>
                </div>
            </div> 
        </div>
    )}
}
export default Admin
