import React,{Component, Fragment}from 'react';
 import axios from 'axios';
import Chart from './chart.component';

export default class SystemGrafth extends Component{
   constructor(props){
       super(props);
       this.state={
        chartData:{n:0},
        Requests:[],
        SvmData:[],
       }
       this.setdata=this.setdata.bind(this);
}
setdata(Svm){
    console.log(Svm)
    axios.get(`http://localhost:3000/Request/Svm/${Svm}`)
    .then((res)=>{ 
        let Amount=0;
        res.data.Req.forEach(Req => {
            Amount+=Req.Amount;
        });
        console.log(Amount)
        return Amount;
    })
}
async SetData(){ 
    let value;
    let array=[0,(1000)];
    console.log(value)
    this.setState({
        chartData:{
            labels:['Free','Used'],
            datasets:[{
             label:"Requests",
             backgroundColor:["rgba(175, 146, 234, 0.65)",
                              "rgba(245, 73, 120, 0.65)",
                              ],
             data:array
            },
            ]
        }
    })
 }
componentDidMount()
{
    let SVMS=[]
    this.props.SVMS.forEach(Svm => {
        SVMS.push(SVM)
    });
    consule.log(SVMS)
}
 Charts(){
    return(
        this.props.SVMS.map((currntSvm,index)=>{
            if(this.state.chartData.n===0)
                {
                    this.setdata(this.props.SVMS[index]);
                    this.SetData();
                }
                
            return(
                    <div className="col-md-6" key={index}>
                        {this.state.chartData.datasets?
                             <Chart type='Pie' title={currntSvm} chartData={this.state}/>
                        :null}
                    </div>
                )
        })
    )

 }

    render(){
        return(
            <Fragment>
                {this.Charts()}
            </Fragment>
        )
    }
}
