import React,{Component}from 'react';
import axios from 'axios';

export default class createUser extends Component{
    
    constructor(props){
        super(props);
        this.onChangeUsername=this.onChangeUsername.bind(this);
        this.onChangeemail=this.onChangeemail.bind(this);
        this.handleChangeSVM=this.handleChangeSVM.bind(this);
        this.setSelectedFile=this.setSelectedFile.bind(this);
        this.onsubmit=this.onsubmit.bind(this);
        
        this.state={
            username:'',
            email:'',
            SVM:'',
            File:'',
        }
    }
    // handeling form fields
    onChangeUsername(e){
        this.setState({
            username:e.target.value
        });
    }
    setSelectedFile(e){
        this.setState({
            File:e.target.value
        });
    }
    onChangeemail(e){
        this.setState({
            email:e.target.value
        });
    }
    handleChangeSVM(e){
        this.setState({
            SVM:e.target.value
        });
    }
    // submiting the form and creating new user in the server
    onsubmit(e){
        e.preventDefault();
        const Request={
            //UserID
            username:this.state.username,
            email:this.state.email,
            SVM:this.state.SVM,
            File:this.state.File,
        }

        console.log(Request);
        axios.post('http://localhost:3000/Request/add',Request)
            .then(res=>{
                console.log(res.data);
                if(res.data)
                {
                this.props.Auth(res.data);
                }
            })
            .catch(err=>console.log("signup err"+err));
    }
    // rendering the formn
    render(){
        return(
            <div className="container">
                <div className="row">
                    <div className="col-md-6 m-auto">
                    <h3 className="text-center display-4 my-4">Request</h3>
            <form onSubmit={this.onsubmit}>
                <div className="form-group  mb-3">
                    <label>Username:</label>
                    <input type='text' required
                    className="form-control" value={this.state.username}
                    onChange={this.onChangeUsername}/>
                </div>
                <div className="form-group  mb-3">
                    <label>email:</label>
                    <input type='email' required
                    className="form-control" value={this.state.email}
                    onChange={this.onChangeemail}/>
                </div>
                <div className="form-group custom-file mb-3">
                    <label for="file" className="custom-file-label">File:</label>
                    <input type='file' required
                    className="form-control custom-file-input" value={this.state.File}
                    onChange={this.setSelectedFile}/>
                </div>
                <div className="form-group">
                    <label>SVM:</label>
                    <select value={this.state.SVM} onChange={this.handleChangeSVM}>
                        <option value="grapefruit">Grapefruit</option>
                        <option value="lime">Lime</option>
                        <option selected value="coconut">Coconut</option>
                        <option value="mango">Mango</option>
                    </select>
                </div>
                <div>
                    <button type="submit" className="btn btn-outline-primary btn-block">Send Request</button>
                </div>
            </form>
                    </div>
                </div>
            
            </div>
        )
    }
}
