import React,{Component,useState}from 'react';
import axios from 'axios';
import Message from '../components/Message'
import {withRouter}from 'react-router-dom'

function AGGREGATEFORM(){
    const [Name, setName] = useState('');
    const [Amount, setAmount] = useState('');
    const [location, setlocation] = useState('');
    const [message,setmessage]=useState('')
    onsubmit()
    {
        console.log(Name)
    }
    console.log(Name)
            return(
            <div className="container">
                {message?<Message msg={this.state.message}/>:''}
                <div className="row">
                    <div className="col-md-6 m-auto">
                    <h3 className="text-center display-4 my-4">Request</h3>
            <form onSubmit={this.onsubmit}>
                <div className="form-group  mb-3">
                    <label>Name:</label>
                    <input type='text' required 
                    className="form-control" value={Name}
                    onChange={setName}/>
                </div>
                <div className="form-group  mb-3">
                    <label>Amount in (GB):</label>
                    <input type='number' required min="0"
                    className="form-control" value={Amount}
                    onChange={setAmount}/>
                </div>
                <div className="form-group  mb-3">
                    <label>location:</label>
                    <input type='text' required 
                    className="form-control" value={location}
                    onChange={setlocation}/>
                </div>
                {this.state.flag===false?
                <div>
                    <button type="submit" className="btn btn-outline-primary btn-block">Send Request</button>     
                </div>
                :
                <button className="btn btn-outline-primary btn-block" type="button" disabled>
                    <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
                    Loading...
                </button>
                }
            </form>
                    </div>
                </div>
            </div>
        )
    }
export default withRouter(AGGREGATEFORM) 


