import React,{Component,useState}from 'react';
import axios from 'axios';
import Download from'./Download.component'
import Progress from '../components/Progress'
import Message from '../components/Message'


export default class createRequest extends Component{
    
    constructor(props){
        super(props);
        this.onChangeUsername=this.onChangeUsername.bind(this);
        this.onChangeemail=this.onChangeemail.bind(this);
        this.handleChangeSVM=this.handleChangeSVM.bind(this);
        this.setSelectedFile=this.setSelectedFile.bind(this);
        this.onsubmit=this.onsubmit.bind(this);
        
        this.state={
            username:'',
            email:'',
            SVM:'',
            File:'',
            flag:false
        }
    }
    // handeling form fields
    onChangeUsername(e){
        this.setState({
            username:e.target.value
        });
    }
    setSelectedFile(e){
        console.log(e)
        this.setState({
            File:e
        });
    }
    onChangeemail(e){
        this.setState({
            email:e.target.value
        });
    }
    handleChangeSVM(e){
        this.setState({
            SVM:e.target.value
        });
    }
    // submiting the form and creating new user in the server
    onsubmit=async(e)=>{
        e.preventDefault();
        console.log(this.state.file)
        await this.upload(this.state.file);
        const Request={
            //UserID
            username:this.state.username,
            email:this.state.email,
            SVM:this.state.SVM,
            File:this.state.File,
        }

        console.log(Request);
        axios.post('http://localhost:3000/Request/add',Request)
            .then(res=>{
                console.log(res.data);
            })
            .catch(err=>console.log("signup err"+err));
    }
    upload= async e=>{
        // e.preventDefault();
        console.log(e)
        const formData=new FormData();
        formData.append('file',e);
        if(e)
        {
          try{
            const res=await axios.post('http://localhost:3000/Request/upload',formData,{
              headers:{
                'content-Type':'multipart/form-data'
              }});
              this.setState({
                file:res.data.file.id
              })
          }catch(err){
            console.log (err)
          }
        }
    }
    // rendering the formn
    render(){
        return(
            <div className="container">
                <div className="row">
                    <div className="col-md-6 m-auto">
                    <h3 className="text-center display-4 my-4">Request</h3>
            <form onSubmit={this.onsubmit}>
                <div className="form-group  mb-3">
                    <label>Username:</label>
                    <input type='text' required
                    className="form-control" value={this.state.username}
                    onChange={this.onChangeUsername}/>
                </div>
                <div className="form-group  mb-3">
                    <label>email:</label>
                    <input type='email' required
                    className="form-control" value={this.state.email}
                    onChange={this.onChangeemail}/>
                </div>
                <div className="form-group mb-3">
                <Download file={this.setSelectedFile} flag={this.state.flag}/>
                {/* {this.state.File?
                <Download file={this.setSelectedFile}/>
                :<div >
                    <div className='custom-file mb-4'>
                        <input type="file" className='custom-file-input' id='customFile' onChange={this.setSelectedFile}/>
                        <label className='custom-file-label' htmlFor='customFile'>{this.state.File.name}</label>
                    </div>
                </div>
                } */}
                </div>
                <div className="form-group">
                    <label>SVM:</label>
                    <select value={this.state.SVM} onChange={this.handleChangeSVM}>
                        <option value="grapefruit">Grapefruit</option>
                        <option value="lime">Lime</option>
                        <option value="coconut">Coconut</option>
                        <option value="mango">Mango</option>
                    </select>
                </div>
                <div>
                    <button type="submit" onclick={this.state.flag=true} className="btn btn-outline-primary btn-block">Send Request</button>
                </div>
            </form>
                    </div>
                </div>
            </div>
        )
    }
}
