import React,{useState}from 'react';
import Login from '../components/UserComponent/Login.component';
import Signup from '../components/UserComponent/createUser.component';

function Login_Signup(props){
    const [flag, setFlag] = useState(false);

    function onClickHandler(e){
        if(flag!==e)
        setFlag(!flag)
    }
        return(
            <div className="text-center container" >
                <div>
                    {flag?
                    <div>
                        <Login className="container" Auth={props.setUser}/>
                    </div>
                    :<div>
                        <Signup className="container" Auth={props.setUser}/>
                    </div>}
                </div>
                    <button onClick={onClickHandler(!flag)} className="btn btn-outline-info btn-block">To Login</button>
            </div>
            )
    
}
export default Login_Signup;
