import React,{Component}from 'react';
import axios from 'axios';
import Download from'./Download.component'

export default class createRequest extends Component{
    
    constructor(props){
        super(props);
        this.onChangeUsername=this.onChangeUsername.bind(this);
        this.onChangeemail=this.onChangeemail.bind(this);
        this.handleChangeSVM=this.handleChangeSVM.bind(this);
        this.setSelectedFile=this.setSelectedFile.bind(this);
        this.onsubmit=this.onsubmit.bind(this);
        const[file,setFile]=useState('');
        const [filename,setFilename]=useState('Choose File');
        const [message,setMessage]=useState('')
        const [uploadPercentage,setUploadPercentage]=useState(0);   
        this.state={
            username:'',
            email:'',
            SVM:'',
            File:'',
        }
    }
    // handeling form fields
    onChangeUsername(e){
        this.setState({
            username:e.target.value
        });
    }
    setSelectedFile(e){
        this.setState({
            File:e.target.value
        });
    }
    onChangeemail(e){
        this.setState({
            email:e.target.value
        });
    }
    handleChangeSVM(e){
        this.setState({
            SVM:e.target.value
        });
    }
    onChange=e=>{
        setFile(e.target.files[0]);
        setFilename(e.target.files[0].name);
    }
    onSubmit= async e=>{
        e.preventDefault();
        const formData=new FormData();
        formData.append('file',file);
        if(file)
        {
          try{
            const res=await axios.post('http://localhost:3000/Request/upload',formData,{
              headers:{
                'content-Type':'multipart/form-data'
              },
              onUploadProgress:ProgressEvent=>{
                setUploadPercentage(
                  parseInt(
                    Math.round((ProgressEvent.loaded*100)/ProgressEvent.total)
                  ));
              }
            });
            const file=res.data.file;
            console.log(file)
      
            setMessage('File Uploaded');
          }catch(err){
            if(err.response.status===500)
              {setMessage("There Was a problam withe the server")}
            else{
              setMessage("err.response.data.msg")
            }
          }
        }
        else{
          setMessage("u need to uplod a file firest")
        }
        }

    // submiting the form and creating new user in the server
    onsubmit(e){
        e.preventDefault();
        const Request={
            //UserID
            username:this.state.username,
            email:this.state.email,
            SVM:this.state.SVM,
            File:this.state.File,
        }

        console.log(Request);
        axios.post('http://localhost:3000/Request/add',Request)
            .then(res=>{
                console.log(res.data);
            })
            .catch(err=>console.log("signup err"+err));
    }
    // rendering the formn
    render(){
        return(
            <div className="container">
                <div className="row">
                    <div className="col-md-6 m-auto">
                    <h3 className="text-center display-4 my-4">Request</h3>
            <form onSubmit={this.onsubmit}>
                <div className="form-group  mb-3">
                    <label>Username:</label>
                    <input type='text' required
                    className="form-control" value={this.state.username}
                    onChange={this.onChangeUsername}/>
                </div>
                <div className="form-group  mb-3">
                    <label>email:</label>
                    <input type='email' required
                    className="form-control" value={this.state.email}
                    onChange={this.onChangeemail}/>
                </div>
                <div className="form-control">
                {message?<Message msg={message}/>:null}
                    <div className='custom-file mb-4'>
                    <input type="file" className='custom-file-input' id='customFile' onChange={onChange}/>
                        <label className='custom-file-label' htmlFor='customFile'>
                        {filename}
                        </label>
                    </div>
                    <Progress percentage={uploadPercentage}/>
                </div>
                <div className="form-group">
                    <label>SVM:</label>
                    <select value={this.state.SVM} onChange={this.handleChangeSVM}>
                        <option value="grapefruit">Grapefruit</option>
                        <option value="lime">Lime</option>
                        <option value="coconut">Coconut</option>
                        <option value="mango">Mango</option>
                    </select>
                </div>
                <div>
                    <button type="submit" className="btn btn-outline-primary btn-block">Send Request</button>
                </div>
            </form>
                    </div>
                </div>
            </div>
        )
    }
}
