import React from 'react';
import Login from '../components/UserComponent/Login.component';
import Signup from '../components/UserComponent/createUser.component';

class Login_Signup extends React.Component{
    constructor(props){
        super(props);
        this.state={flag:false,ButtonMsg:'To Sign Up'};
        this.ToLogin=this.ToLogin.bind(this);
    }

    componentDidMount(){
            this.setState({flag:!this.state.flag})
    }
    ToLogin(flag)
    {
        this.setState({flag:flag})
        if(flag=true){
            this.setState({ButtonMsg:'To Login'});
        }else{
            this.setState({ButtonMsg:'To Sign Up'})
        }

    }
    render()
    {
        return(
            <div className="container mr-" >
                <div className="row ">
                    {this.state.flag?
                    <div className="col-md-7">
                        <Login Auth={this.props.setUser}/>
                    </div>
                    :<div className="col-md-7">
                        <Signup  Auth={this.props.setUser}/>
                    </div>}
                    <div className="col-md-7">
                        <br/>
                    <button onClick={()=>{this.ToLogin(!this.state.flag)}} className="btn btn-outline-info btn-block">{this.state.ButtonMsg}</button>
                    </div> 
               </div>
            </div>
            )
    }
}
export default Login_Signup;
