import axios from 'axios';
import fileDownload from '../../node_modules/js-file-download'
{/* <button href="https://http://localhost:3000/Request"/> */}
export default class FileTest extends Component{
  constructor(props){
    super(props);
    this.onFormSubmit=this.onFormSubmit.bind(this);
    this.onChange=this.onChange.bind(this);
    
    this.state={
        selectedFile: null
    }
}
// handeling form fields
onFormSubmit(e){
  e.preventDefault();
  const formData = new FormData();
  formData.append('myfile',this.state.file);
  const config = {
      headers: {
          'content-type': 'multipart/form-data'
      }
  };
  axios.post("http://localhost:3000/Request/upload",formData,config)
      .then((response) => {
          alert("The file is successfully uploaded");
      }).catch((error) => {
  });
}

onChange(e) {
  this.setState({file:e.target.files});
}

    render(){
        return(
        <div className="container">
          <div className="row">
            <div className="col-md-6 m-auto">
              <form onSubmit={this.onFormSubmit} encType="multipart/form-data">
              <div className="form-group custom-file mb-3">
                     <label className="custom-file">{this.state.File}</label>
                     <input type="file" className="custom-file-input" name="myImage" onChange= {this.onChange} />
                </div>
                <div>
                    <button onSubmit={this.onsubmit} type="submit" className="btn btn-outline-primary btn-block">Send Request</button>
                </div>
              </form>
            </div>
          </div>
        </div>
        )
    }
}
