import React,{Component}from 'react';
import axios from 'axios';
import fileDownload from '../../node_modules/js-file-download'
{/* <button href="https://http://localhost:3000/Request"/> */}
export default class FileTest extends Component{
  constructor(props){
    super(props);
    this.setSelectedFile=this.setSelectedFile.bind(this);
    this.onsubmit=this.onsubmit.bind(this);
    
    this.state={
        selectedFile: null
    }
}
// handeling form fields
setSelectedFile(e){
  console.log(e.target.files[0])
    this.setState({
      selectedFile: e.target.files[0],
      loaded: 0,
    });
}
// submiting the form and creating new user in the server
onsubmit(e){
    e.preventDefault();
    const data = new FormData() 
    data.append('file', this.state.selectedFile)
    const Request={
        //UserID
        username:'Test',
        email:"Test",
        SVM:"Test",
        File:data,
    }
    console.log(Request.File);
    // axios.post('http://localhost:3000/Request/add',Request)
    //     .then(res=>{
    //         console.log(res.data);
    //     })
    //     .catch(err=>console.log("signup err"+err));
}

    render(){
        return(
        <div className="container">
          <div className="row">
            <div className="col-md-6 m-auto">
              <form onSubmit={this.onsubmit} encType="multipart/form-data">
              <div className="form-group custom-file mb-3">
                    {/* <label className="custom-file-label">{this.state.File}</label> */}
                     <input type="file" name="file" onChange={this.setSelectedFile}/>
                </div>
                <div>
                    <button type="submit" className="btn btn-outline-primary btn-block">Send Request</button>
                </div>
              </form>
            </div>
          </div>
        </div>
        )
    }
}
