import React,{Component, Fragment}from 'react';
 import axios from 'axios';
import Chart from './chart.component';

export default class AdminSpecs extends Component{
   constructor(props){
       super(props);
       this.state={
        chartData:{},
        Requests:[],
        SvmData:[],
       }
}
async SetChartData(Svm){
    let SvmData;
    await axios.get(`http://localhost:3000/Request/Svm/${Svm}`)
    .then((res)=>{SvmData=res.data.Req})
    let Responses=[0,0]
     SvmData.forEach(Req => {
        if(Req.status==="pending")
            Responses[0]++;
        else
            Responses[1]++;
    });
    const Res=Responses;
    return({
        chartData:{
            labels:['Freespace','UsedSpace'],
            datasets:[{
             label:"Requests",
             backgroundColor:["rgba(200, 0, 120, 0.65)",
                              "rgba(60, 0, 200, 0.65)",
                              "rgba(255, 12, 60, 0.75)",
                              ],
             data:[1,3]
            },
            ]
        }
    })
}
 Charts(){
    return(
        this.props.SVMS.map((currntSvm,index)=>{
            return(
                    <div className="col-md-6" key={index}>
                        <Chart type='Pie' title={currntSvm}chartData={this.SetChartData(currntSvm)}/>
                    </div>
                )
        })
    )

 }

    render(){
        console.log(this.props)
        return(
            <Fragment>
                {this.Charts()}
            </Fragment>
        )
    }
}
