import React,{Component}from 'react';
import axios from 'axios';


export default class ArcitectPage extends Component{
    constructor(props){
        super(props);
        this.state={
            username:'',
            email:'',
            Svm:'',
            File:''
        }
        this.onClickHendler=this.onClickHendler.bind(this);
        this.UptateStatus=this.UptateStatus.bind(this);
    }
    onClickHendler(){
        console.log(this.state.File);
        axios.get(`http://localhost:3000/File/${this.state.File}`)
        .then(res=>{console.log(res)})
    }
    UptateStatus(status){
        const Status=status;
        axios.post(`http://localhost:3000/Request/status/${this.props.match.params.id}`,Status)
        .then(res=>console.log(res))

    }
    componentDidMount(){
        //getRequestByID
        console.log(this.props.match.params.id)
        axios.get(`http://localhost:3000/Request/${this.props.match.params.id}`)
        .then(res=>{
            this.setState({
                username:res.data.Req.username,
                email:res.data.Req.email,
                Svm:res.data.Req.SVM,
                File:res.data.Req.File,
                key:res.data.Req._id
            });
        })
    }
    
    render(){
        let downlodURI=`http://localhost:3000/File/${this.state.File}`;
        return(
        <div className="row">
            <div className="w-50">
                <ul className="font-weight-light">
                    <li>{this.state.username}</li>
                    <li>{this.state.email}</li>
                    <li>{this.state.Svm}</li>
                    <a href={downlodURI} className='btn btn-primary' download>Download File</a>
                </ul>
                <div className="d-flex justify-content-center">
                    <button onClick={()=>this.UptateStatus('Accepted')} className="btn btn-success">Accept</button>
                    <button onClick={()=>this.UptateStatus('Decline')}className="btn btn-danger">Decline</button>
                </div>
            </div>
            <div className="w-50 ">
                <ul className="font-weight-light">
                    <li>serverone</li>
                    <li>servertwo</li>
                    <li>serverthree</li>
                </ul>
            </div>
        </div>
        )
    }
}
