import React, { Component} from'react'
import axios from 'axios'


class StorageGraft extends Component{
    constructor(props){
        super(props);
        this.state={
            Aggre:'',
            SVMSArray:[],
        }
    }
    componentDidMount(){
        let Aggre;
        let SVMSArray;
        console.log(this.props)
        axios.get(`Aggregate/Aggre/${this.props.Aggre}`)
        .then(async(res)=>{
            Aggre=res.data.res[0];
            await axios.get(`SvmRoute/SvmByAggreName/${this.props.Aggre}`)
            .then((res)=>{
            console.log(res.data.res)
            SVMSArray=res.data.res

            });
            this.setState({Aggre:Aggre,SVMSArray:SVMSArray})
         });
    
    }
    getRandomColor() {
        var letters = '0123456789ABCDEF';
        var color = '#';
        for (var i = 0; i < 6; i++) {
          color += letters[Math.floor(Math.random() * 16)];
        }
        return color;
      }
    getBar(){
        return(
            this.state.SVMSArray.map((SVM)=>{
                let percentage=0;
                if(SVM.used!==0)
                    {
                        let Temp=((SVM.total/this.state.Aggre.total)*100);
                        percentage=Temp.toFixed(3);
                    }
                return(
                    <div key={SVM._id} className="progress-bar progress-bar-striped"
                    role="progressbar"
                    data-toggle="tooltip" data-placement="bottom" title={`${SVM.Name}--${SVM.total}GB`}
                    style={{width:`${percentage-0.5}%`,backgroundColor:`${this.getRandomColor()}`}}>
                        {percentage}%
                </div>
                )
            })
        )
    }
    
    render(){
        console.log(this.state.Aggre)
        return(
            <>
            {this.state.Aggre?
                <div className="progress">
                    {this.state.Aggre.used!==0?
                    <>
                        {this.state.Aggre.total!==0?
                            <div className="progress-bar progress-bar-striped bg-success"
                                role="progressbar"
                                data-toggle="tooltip" data-placement="bottom" title={`Free Space--${this.state.Aggre.total-this.state.Aggre.used}GB`}
                                style={{width:`${((this.state.Aggre.used/this.state.Aggre.total)*-100)+100}%`}}>
                                    {((this.state.Aggre.used/this.state.Aggre.total)*-100)+100}%
                            </div>
                        :       <div className="progress-bar progress-bar-striped bg-danger tooltip"
                                role="progressbar"
                                data-toggle="tooltip" data-placement="bottom" title={'Data Error'}
                                style={{width:`${100}%`}}>
                                {Error}%
                                </div>
                            }
                    </>
                    :<div className="progress-bar progress-bar-striped bg-success"
                             role="progressbar"
                             data-toggle="tooltip" data-placement="bottom" title={`Free Space--${this.state.Aggre.total-this.state.Aggre.used}GB`}
                             style={{width:`${100}%`}}>
                                {100}%
                        </div>
                    }
                    {this.getBar()}
                </div>
                :
                <div className="spinner-grow spinner-grow-sm" role="status">
                    <span className="sr-only">Loading...</span>
                </div>
                }
            </>
            );
    }

}
export default StorageGraft
