import React,{Component}from 'react';
import axios from 'axios';


export default class ArcitectPage extends Component{
    constructor(props){
        super(props);
        this.state={
            username:'',
            email:'',
            Svm:'',
            File:''
        }
        this.onClickHendler=this.onClickHendler.bind(this);
    }
    onClickHendler(){
        axios.get(`http://localhost:3000/File/${this.state.File}`)
        .then(res=>{res.blob();
        }).then(blob => {
            const href = window.URL.createObjectURL(blob);
            const a = this.linkRef.current;
            a.download = 'Lebenslauf.pdf';
            a.href = href;
            a.click();
            a.href = '';
        }).catch(err => console.error(err));
    }
    componentDidMount(){
        //getRequestByID
        console.log(this.props.match.params.id)
        axios.get(`http://localhost:3000/Request/${this.props.match.params.id}`)
        .then(res=>{
            this.setState({
                username:res.data.Req.username,
                email:res.data.Req.email,
                Svm:res.data.Req.SVM,
                File:res.data.Req.File,
                key:res.data.Req._id
            });
        })
    }
    onclick(){
        axios.get(`http://localhost:3000/File/${this.state.file}`)
            .then(res=>console.log(res))
    }

    render(){
        return(
        <div className="row">
            <div className="w-50">
                <ul className="font-weight-light">
                    <li>{this.state.username}</li>
                    <li>{this.state.email}</li>
                    <li>{this.state.Svm}</li>
                    <li onClick={this.onClickHendler} className='btn btn-primary'>Download File</li>
                </ul>
                <div className="d-flex justify-content-center">
                    <button className="btn btn-success">Accept</button>
                    <button className="btn btn-danger">Decline</button>
                </div>
            </div>
            <div className="w-50 ">
                <ul className="font-weight-light">
                    <li>bla</li>
                </ul>
            </div>
        </div>
        )
    }
}
