import React,{useState}from 'react';
import Login from '../components/UserComponent/Login.component';
import Signup from '../components/UserComponent/createUser.component';

function Login_Signup(){
    const [flag, setFlag] = useState(false);
        return(
            <div className="text-center container" >
                    {this.state.flag?
                    <div>
                        <Login err={this.state.err} className="container" Auth={this.props.setUser}/>
                        <button className="btn btn-outline-info btn-block" onClick={setFlag(false)}>To SignUp</button>
                    </div>
                    :<div>
                        <Signup className="container" Auth={this.props.setUser}/>
                        <button className="btn btn-outline-info btn-block" onClick={setFlag(true)}>To Login</button>
                    </div>}
            </div>
            )
    
}
export default Login_Signup;
