import React,{Component, Fragment}from 'react';
 import axios from 'axios';
import Chart from './chart.component';

export default class AdminSpecs extends Component{
   constructor(props){
       super(props);
       this.state={
        chartData:{},
        Requests:[],
        SvmData:[],
       }
}
SetData(){ 
    let Responses=[0,2,]
    return({
        chartData:{
            labels:['Free','Used'],
            datasets:[{
             label:"Requests",
             backgroundColor:["rgba(175, 146, 234, 0.65)",
                              "rgba(100, 200, 10, 0.65)",
                              ],
             data:Responses
            },
            ]
        }
    })
 }
 AxsiosGetReqFromSvms(Svm){
    axios.get(`http://localhost:3000/Request/Svm/${Svm}`)
    .then((res)=>{this.setState({SvmData:res.data.Req})})
 }
 SetChartData(){

    let Responses=[0,0]
    this.state.SvmData.forEach(Req => {
        if(Req.status==="pending")
            Responses[0]++;
        else
            Responses[1]++;
    });
    console.log(Responses)
}
 Charts(){
    return(
        this.props.SVMS.map((currntSvm,index)=>{
            return(
                    <div className="col-md-6" key={index}>
                        <Chart type='Pie' title={currntSvm} chartData={this.SetData()}/>
                    </div>
                )
        })
    )

 }

    render(){
        return(
            <Fragment>
                {this.Charts()}
            </Fragment>
        )
    }
}
