import React,{Component, Fragment}from 'react';
 import axios from 'axios';
import Chart from './chart.component';

export default class AdminSpecs extends Component{
   constructor(props){
       super(props);
       this.state={
        chartData:{},
        Requests:[],
        SvmData:[],
       }
}
SetData(){ 
    let Responses=[0,2,3]
    return({
        chartData:{
            labels:['pending for response','accepted','declined'],
            datasets:[{
             label:"Requests",
             backgroundColor:["rgba(200, 200, 15, 0.65)",
                              "rgba(100, 200, 10, 0.65)",
                              "rgba(255, 12, 60, 0.75)",
                              ],
             data:Responses
            },
            ]
        }
    })
 }
 componentDidUpdate(){
     console.log('hello')
 }
 SetChartData(){
    let SvmData;

    let Responses=[0,0]
     SvmData.forEach(Req => {
        if(Req.status==="pending")
            Responses[0]++;
        else
            Responses[1]++;
    });
    console.log(Responses)
}
 Charts(){
    return(
        this.props.SVMS.map((currntSvm,index)=>{
            return(
                    <div className="col-md-6" key={index}>
                        <Chart type='Pie' title={currntSvm} chartData={this.SetData()}/>
                    </div>
                )
        })
    )

 }

    render(){
        console.log(this.props)
        return(
            <Fragment>
                {this.Charts()}
            </Fragment>
        )
    }
}
