import React from 'react';
import Login from '../components/UserComponent/Login.component';
import Signup from '../components/UserComponent/createUser.component';

class Login_Signup extends React.Component{
    constructor(props){
        super(props);
        this.state={flag:false};
        this.onclick=this.onclick.bind(this);
    }

    componentDidMount(){
        if(this.state.flag!==!this.state.flag)
            this.setState({flag:true})
    }
    onclick(e)
    {
        console.log(e)
    }
    render()
    {
        return(
            <div className="text-center container" >
                <div>
                    {this.state.flag?
                    <div>
                        <Login className="container" Auth={this.props.setUser}/>
                    </div>
                    :<div>
                        <Signup className="container" Auth={this.props.setUser}/>
                    </div>}
                </div>
                    <button onClick={this.onclick(!this.state.flag)} className="btn btn-outline-info btn-block">To Login</button>
            </div>
            )
    }
}
export default Login_Signup;
