import React,{Component}from 'react';
import axios from 'axios';
import fileDownload from '../../node_modules/js-file-download'
{/* <button href="https://http://localhost:3000/Request"/> */}
export default class FileTest extends Component{
  constructor(props){
    super(props);
    this.setSelectedFile=this.setSelectedFile.bind(this);
    this.onsubmit=this.onsubmit.bind(this);
    
    this.state={
        selectedFile: null
    }
}
// handeling form fields
setSelectedFile(e){
    this.setState({
      selectedFile: e.target.files[0],
      loaded: 0,
    });
}
// submiting the form and creating new user in the server
onsubmit(e){
    e.preventDefault();
    const data = new FormData() 
    data.append('file', this.state.selectedFile)
    const Request={
        File:data,
    }
    console.log(Request.File);
    axios.post('http://localhost:3000/Request/upload',Request)
        .then(res=>{
            console.log(res.data);
        })
        .catch(err=>console.log("signup err"+err));
}

    render(){
        return(
        <div className="container">
          <div className="row">
            <div className="col-md-6 m-auto">
            <form onSubmit={this.onFormSubmit}>
              <h1>File Upload</h1>
              <input type="file" className="custom-file-input" name="myImage" onChange= {this.onChange} />
              {console.log(this.state.file)}
              <button className="upload-button" type="submit">Upload to DB</button>
            </form>
            </div>
          </div>
        </div>
        )
    }
}
