import React,{Component}from 'react';
import RequsetCard from '../components/RequestCard.component' 
import axios from 'axios';
import AdminastorChart from'../components/ArchitectChart.component'

export default class ArcitectPage extends Component{
    constructor(props){
        super(props);
        
        this.state={
            Requests:[],
            PendingReq:[],
            NumOfPages:0,
            next:4,
            prev:0
        };
    }
    
    componentDidMount(){
        axios.get(`Request/`)
        .then(res=>{
            let PendingReq=[];
            let num=0;
            res.data.res.forEach(Req=>{
                if(Req.status==="pending")
                {
                    num++;
                    PendingReq.push(Req);
                }
            })
                if(res.data.err===null){
                    this.setState({Requests:res.data.res,NumOfPages:parseInt(num),PendingReq:PendingReq})
                }else{
                    console.log(res.data.err)
                }
            })
    }

    SetChartData(){
        let Responses=[0,0,0]
        this.state.Requests.forEach(Req => {
            if(Req.status==="pending")
                Responses[0]++;
            if(Req.status==='Accepted')
                Responses[1]++;
            if(Req.status==="Decline")
                Responses[2]++;
        });
        return Responses
    }
    PageNav(){

    }
    RequestsList(){
        return this.state.PendingReq.map((currentRequests,index)=>{

            console.log(index)
            console.log(this.state.prev+"||"+this.state.next)
            console.log(((index>=this.state.prev)&&(index<this.state.next)))
            if((index>=this.state.prev)&&(index<this.state.next)){
                console.log(currentRequests)
                return(
                    <div className="col-md-2 mt-2"key={currentRequests._id} >
                        <RequsetCard Username={currentRequests.username}  email={currentRequests.email}
                                    File={currentRequests.File} type={currentRequests.type} Name={currentRequests.Name}
                                    id={currentRequests._id} Amount={currentRequests.Amount}
                                    key={currentRequests._id}/>
                    </div>)
            }
        })
    }
            
    render(){
        console.log(this.state.index)
        const Ur=JSON.parse(localStorage.getItem('user'));
        return(
        <div>
            {Ur.Ar?null:window.location.href = '/UKnoob'}
            <AdminastorChart Requsets={this.state.Requests} ChartData={this.SetChartData()}/>
            <div className="row container-fluid d-flex justify-content-center">
                <div className="col-md-2 mt-2 pt-3">
                    {/* <br/> */}
                    <button type="button" className="btn btn-outline-info btn-lg ">
                        <svg xmlns="http://www.w3.org/2000/svg" width="3rem" height="3rem" fill="currentColor" class="bi bi-arrow-left" viewBox="0 0 16 16">
                            <path fill-rule="evenodd" d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8z"/>
                        </svg>
                    </button>
                    {/* <br/> */}
                </div>
                  {this.RequestsList()}
                <div className="col-md-2 mt-2">
                  <button type="button" className="btn btn-outline-info btn-lg align-items-center ">
                    <svg xmlns="http://www.w3.org/2000/svg" width="3rem" height="3rem" fill="currentColor" class="bi bi-arrow-right" viewBox="0 0 16 16">
                        <path fill-rule="evenodd" d="M1 8a.5.5 0 0 1 .5-.5h11.793l-3.147-3.146a.5.5 0 0 1 .708-.708l4 4a.5.5 0 0 1 0 .708l-4 4a.5.5 0 0 1-.708-.708L13.293 8.5H1.5A.5.5 0 0 1 1 8z"/>
                    </svg>
                  </button>
                </div>
            </div>
           
        </div>
        )
    }
}
