import React,{Component}from 'react';
import axios from 'axios';
import Message from '../components/Message'
import {withRouter}from 'react-router-dom'
import Progress from '../components/Progress'


class createRequest extends Component{
    
    constructor(props){
        super(props);
        this.state={
            Name:'',
            Amount:'',
            location:'',
            AGGREGATE:[{}],
            File:File,
            flag:false
        }
        this.onChangeName=this.onChangeName.bind(this);
        this.onChangeAmount=this.onChangeAmount.bind(this);
        this.location=this.location.bind(this);
        this.setSelectedFile=this.setSelectedFile.bind(this);
        this.AGGREGATE=this.AGGREGATE.bind(this);
        this.onclicked=this.onclicked.bind(this);
        this.onsubmit=this.onsubmit.bind(this);
    }
    componentDidMount(){
        axios.get('http://localhost:3000/AGGREGATE')
        .then((res)=>this.setState({AGGREGATE:res.data}))
    }

    // handeling form fields
    onChangeName(e){
        this.setState({Name:e.target.value});
    }
    onChangeAmount(e){
        this.setState({
            Amount:e.target.value
        });
    }
    location(e){
        this.setState({
            location:e.target.value
        });
    }
    AGGREGATE(e){
        this.setState({AGGREGATE:e.target.value})
    }
    handleChange(e){
        console.log(e.target.value)
    }
    setSelectedFile(e){
        this.setState({
            File:e.target.files[0]
        });
    }
    onclicked(e){
        this.setState({flag:true})
    }
    // submiting the form and creating new user in the server
    onsubmit=async(e)=>{
        e.preventDefault();
        // this.onclicked();
        // const FileID=await this.upload(this.state.File);
        // console.log(FileID);
        const SvmRequest={
            Name:this.state.Name,
            Amount:this.state.Amount,
            AGGREGATE:[],
            File:File,
        }

        console.log(SvmRequest);
        axios.post('http://localhost:3000/SvmRoute/add',Request)
            .then(res=>{
                this.setState({message:"Request send"})
                this.props.history.push("/Home");
            })
            .catch(err=>{this.setState({message:"Error"+err})});
    }
    upload= async e=>{
        // e.preventDefault();
        const formData=new FormData();
        formData.append('file',e);
        if(e)
        {
            try{
                  const res=await axios.post('http://localhost:3000/File/upload',formData,{
                    headers:{
                      'content-Type':'multipart/form-data'
                    },
                    onUploadProgress:ProgressEvent=>{
                      this.setState({uploadPercentage:
                        parseInt(
                          Math.round((ProgressEvent.loaded*100)/ProgressEvent.total)
                        )});
                    }
                  });
                  return(res.data.file.filename);
          }catch(err){
            console.log (err)
          }
        }
    }
    render(){
        return(
            <div className="container">
                {this.state.message?<Message msg={this.state.message}/>:''}
                <div className="row">
                    <div className="col-md-6 m-auto">
                    <h3 className="text-center display-4 my-4">Create New AGGREGATE</h3>
            <form onSubmit={this.onsubmit}>
                <div className="form-group  mb-3">
                    <label>Name:</label>
                    <input type='text' required 
                    className="form-control" value={this.state.Name}
                    onChange={this.onChangeName}/>
                </div>
                <div className="form-group  mb-3">
                    <label>Amount in (GB):</label>
                    <input type='number' required min="0"
                    className="form-control" value={this.state.Amount}
                    onChange={this.onChangeAmount}/>
                </div>
                <div className="form-group mb-3">
                <div>
                    <div className='custom-file mb-4'>
                        <input type="file" className='custom-file-input' id='customFile' onChange={this.setSelectedFile}/>
                        <label className='custom-file-label' htmlFor='customFile'>
                        {this.state.File.name}
                        </label>
                    </div>
                        <Progress percentage={this.state.uploadPercentage}/>
                    </div>
                </div>
                <div className="form-group">
                    <label>AGGREGATE:</label>
                    <select value={this.state.AGGREGAT} onChange={this.handleChange}>
                        {this.state.AGGREGATE.map((AGGREGATE,index) => (
                            <option key={index} value={AGGREGATE._id}>{AGGREGATE.name}</option>
                        ))}
                    </select>
                </div>
                {this.state.flag===false?
                <div>
                    <button type="submit" className="btn btn-outline-primary btn-block">Send Request</button>     
                </div>
                :
                <button className="btn btn-outline-primary btn-block" type="button" disabled>
                    <span className="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>
                    Loading...
                </button>
                }
            </form>
                    </div>
                </div>
            </div>
        )
    }
}
export default withRouter(createRequest)
