import React,{Component, Fragment}from 'react';
 import axios from 'axios';
import Chart from './chart.component';

export default class SystemGrafth extends Component{
   constructor(props){
       super(props);
       this.state={
        chartData:{},
        Requests:[],
        SvmData:[],
       }
}
async SetData(){ 
    let Responses=[3,1];
    await this.props.SVMS.forEach(async(currntSvm)=>{
        let temp=await this.SetChartData(currntSvm);
        console.log(temp)
    })

    return({
        chartData:{
            labels:['Free','Used'],
            datasets:[{
             label:"Requests",
             backgroundColor:["rgba(175, 146, 234, 0.65)",
                              "rgba(245, 73, 120, 0.65)",
                              ],
             data:[3,2]
            },
            ]
        }
    })
 }
 SetChartData(Svm){
    axios.get(`http://localhost:3000/Request/Svm/${Svm}`)
    .then((res)=>{ 
        let Amount=0;
        res.data.Req.forEach(Req => {
            Amount+=Req.Amount;
        });
        let Max=(1000 -Amount);
        let Responses=[Amount,Max]
        console.log(Responses)
        return Responses
    })

}
 Charts(){
    return(
        this.props.SVMS.map((currntSvm,index)=>{
            return(
                    <div className="col-md-6" key={index}>
                        <Chart type='Pie' title={currntSvm} chartData={this.SetData()}/>
                    </div>
                )
        })
    )

 }

    render(){
        return(
            <Fragment>
                {this.Charts()}
            </Fragment>
        )
    }
}
